  `add_subsection` longtext,
  `veg` tinyint DEFAULT '0',
  `username` longtext,
  `note` longtext,
  `rating` int DEFAULT NULL,
  `review` longtext DEFAULT (json_array()),
  `cook` longtext DEFAULT (json_array()),
  `lent` tinyint DEFAULT '0',
  `cheap` tinyint DEFAULT '0',
  `calorie` tinyint DEFAULT '0',
  `festive` tinyint DEFAULT '0',
  `unusual` tinyint DEFAULT '0',
  `spicy` tinyint DEFAULT '0',
  `exotic` tinyint DEFAULT '0',
  `section` longtext,
  `kitchen` varchar(50) DEFAULT '',
  `marinade` tinyint DEFAULT '0',
  `bbq` tinyint DEFAULT '0',
  `useremail` varchar(45) DEFAULT NULL,
  `ingredients_arr` longtext DEFAULT (json_array()),
  `calories` float DEFAULT NULL,
  `proteins` float DEFAULT NULL,
  `fats` float DEFAULT NULL,
  `carbohydrates` float DEFAULT NULL,
  `cook_arr` longtext,
  `links` longtext DEFAULT (json_array()),
  `gluten` tinyint DEFAULT '0',
  `lactose` tinyint DEFAULT '0',
  `complexity` float DEFAULT NULL,
  `hour` float DEFAULT NULL,
  `minutes` float DEFAULT NULL,
  `price` float DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `id_UNIQUE` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=37 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;
