CREATE TABLE `snacks` (
  `id` int NOT NULL AUTO_INCREMENT,
  `name` varchar(80) DEFAULT NULL,
  `ingredients` longtext,
  `cooking` longtext,
  `img_main` longtext,
  `date` datetime DEFAULT NULL,
  `type` longtext,
  `add_section` longtext,
  `add_subsection` longtext,
