  `cook` longtext DEFAULT (json_array()),
  `lent` tinyint DEFAULT '0',
  `cheap` tinyint DEFAULT '0',
  `calorie` tinyint DEFAULT '0',
  `festive` tinyint DEFAULT '0',
  `unusual` tinyint DEFAULT '0',
  `spicy` tinyint DEFAULT '0',
  `exotic` tinyint DEFAULT '0',
  `section` longtext,
