INSERT INTO `users` VALUES (255,'olawitcher@gmail.com','olawitcher@gmail.com','$2a$10$lIya8mY61O5aqq5S4z7d0OlLQbXDOvJ02HXc2yVFG/lMm4mvPTXnG',NULL,NULL,'Гюзель','Хайбулина',NULL,'Halla','1749998984778agJUdvig','444','pppp','666','uuu','{\"saved\":[{\"recipe\":\"http://localhost:3000/recipes/recipe/sort?type=drink&subtype=cocktails&id=3\",\"name\":\"Голубая лагуна безалкогольный\",\"img\":\"gXgPTse2--0.jpg\",\"like\":\"false\"},{\"recipe\":\"http://localhost:3000/recipes/recipe/sort?type=dough&subtype=sweet&id=116\",\"name\":\"Орешки с вареной сгущенкой\",\"img\":\"1734523830336-1-114579-7300.jpg\",\"like\":\"false\"},{\"recipe\":\"http://localhost:3000/recipes/recipe/sort?type=second&subtype=meat&id=7\",\"name\":\"Манты по-Уйгурски\",\"img\":\"1731507336787-0-141793-1852.jpg\",\"like\":\"false\"},{\"recipe\":\"http://localhost:3000/recipes/recipe/sort?type=snacks&subtype=salad&id=3\",\"name\":\"Помидоры с брынзой и зеленью\",\"img\":\"pomidiry_far.jpg\",\"like\":\"false\"},{\"recipe\":\"http://localhost:3000/recipes/recipe/sort?type=drink&subtype=tea&id=39\",\"name\":\"Чай Улун\",\"img\":\"1748100553254-0-image.jpg\"}]}','admin','','{\"visited\":[{\"page\":\"http://localhost:3000/recipes/recipe/sort?type=salad&subtype=fish&id=33\",\"img\":\"/upload/recipe_main_img/1733321859526-0-2024-01-05_19-06-27.jpg\",\"name\":\"Селедка под шубой\"},{\"page\":\"http://localhost:3000/recipes/recipe/sort?type=main&subtype=marinade&id=242\",\"img\":\"https://storage.yandexcloud.net/vseresepty/1754998724679LiSaE6t7\",\"name\":\"Заготовка огурцы ′Пикули′ на зиму\"},{\"page\":\"http://localhost:3000/recipes/recipe/sort?type=second&subtype=bbq&id=169\",\"img\":\"/upload/recipe_main_img/1741107135349-0-side-view-lamb-kebab-with-baked-potato-vegetables-wooden-board_140725-10183.jpg\",\"name\":\"Шашлык из говядины по-азербайджански\"},{\"page\":\"http://localhost:3000/recipes/recipe/sort?type=snacks&subtype=can&id=66\",\"img\":\"/upload/recipe_main_img/1736877618447-0-2023-101696862359.jpg